import React, { useState, useEffect } from 'react';
import SaveButton from '../../../components/buttons/SaveButton';
import CancelButton from '../../../components/buttons/CancelButton';
import PrintButton from '../../../components/buttons/PrintButton';
import NewButton from '../../../components/buttons/NewButton';
import Input from '../../../components/form/Input';
import Select from '../../../components/form/Select';
import DoubleInput from '../../../components/form/DoubleInput';
import ModalCloseButton from '../../../components/buttons/ModalCloseButton';
import ModalHeading from '../../../components/headings/ModalHeading';
import RefreshButton from '../../../components/buttons/RefreshButton';
import TableRow from '../../../components/TableRow';
import EditButton from '../../../components/buttons/EditButton';
import DeleteButton from '../../../components/buttons/DeleteButton';
import { toast } from 'react-toastify';
import DashboardPageHeading from '../../../components/headings/DashboardPageHeading';
import AddModal from '../../../components/modals/AddModal';
import { BiEdit } from 'react-icons/bi';

const API_BASE_URL = 'http://localhost:5000/api/products/nonPharmacy';

// Custom EditButton component for NonPharmacy products
const EditNonPharmacyButton = ({ id, onUpdate }) => {
    const [product, setProduct] = useState({});
    const [categories, setCategories] = useState([]);
    const [companies, setCompanies] = useState([]);
    const [unitTypes, setUnitTypes] = useState([]);
    const [modalId, setModalId] = useState(`update-non-pharmacy-product-${id}`);

    // Update modalId when id changes
    useEffect(() => {
        setModalId(`update-non-pharmacy-product-${id}`);
    }, [id]);

    useEffect(() => {
        if (!id) return;
        
        console.log("Fetching non-pharmacy product with ID:", id);
        console.log("API URL:", `${API_BASE_URL}/${id}`);
        
        fetch(`${API_BASE_URL}/${id}`, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => {
            console.log("API Response status:", res.status);
            if (!res.ok) throw new Error(`HTTP error! Status: ${res.status}`);
            return res.json();
        })
        .then(data => {
            console.log("Fetched product data:", data);
            setProduct(data);
        })
        .catch(error => {
            console.error("Error fetching product:", error);
            toast.error("Failed to load product data");
        });
    }, [id]);

    useEffect(() => {
        fetch('http://localhost:5000/api/setup/categories', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => {
            if (!res.ok) throw new Error(`HTTP error! Status: ${res.status}`);
            return res.json();
        })
        .then(data => {
            setCategories(data);
        })
        .catch(error => {
            console.error("Error fetching categories:", error);
            toast.error("Failed to load categories");
        });
    }, []);

    useEffect(() => {
        fetch('http://localhost:5000/api/setup/companies', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => {
            if (!res.ok) throw new Error(`HTTP error! Status: ${res.status}`);
            return res.json();
        })
        .then(data => {
            setCompanies(data);
        })
        .catch(error => {
            console.error("Error fetching companies:", error);
            toast.error("Failed to load companies");
        });
    }, []);

    useEffect(() => {
        fetch('http://localhost:5000/api/setup/unitTypes', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => {
            if (!res.ok) throw new Error(`HTTP error! Status: ${res.status}`);
            return res.json();
        })
        .then(data => {
            setUnitTypes(data);
        })
        .catch(error => {
            console.error("Error fetching unit types:", error);
            toast.error("Failed to load unit types");
        });
    }, []);

    const handleUpdate = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        const productDetails = Object.fromEntries(formData.entries());
        
        console.log("Updating product with ID:", id);
        console.log("Update URL:", `${API_BASE_URL}/${id}`);
        console.log("Update data:", productDetails);

        fetch(`${API_BASE_URL}/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify(productDetails),
        })
        .then(response => {
            console.log("Update response status:", response.status);
            if (!response.ok) {
                // Try to get more details about the error
                return response.text().then(text => {
                    console.error("Error response:", text);
                    throw new Error('Update failed: ' + text);
                });
            }
            return response.json();
        })
        .then(data => {
            console.log("Update successful:", data);
            toast.success(`Product updated successfully`);
            document.getElementById(modalId).checked = false;
            if (onUpdate) onUpdate(); // Refresh the product list
        })
        .catch(error => {
            console.error('Error updating product:', error);
            toast.error('Failed to update product: ' + error.message);
        });
    };

    return (
        <>
            <input type="checkbox" id={modalId} className="modal-toggle" />
            <label htmlFor={modalId} className="modal cursor-pointer">
                <label className="modal-box lg:w-7/12 md:w-10/12 w-11/12 max-w-4xl relative" htmlFor="">
                    <ModalCloseButton modalId={modalId} />
                    <ModalHeading modalHeading={`Update ${product.Product_name || 'Product'}`} />

                    <form onSubmit={handleUpdate}>
                        <div className='grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-x-4 gap-y-1 mb-2'>
                            <Input title={'Product Name'} type='text' value={product.Product_name} name='Product_name' isRequired='required' />
                            <Select title={'Category'} name='Category' isRequired='required' options={categories.map(c => c.Name)} />
                            <Select title={'Company'} name='Company' isRequired='required' options={companies.map(c => c.Name)} />
                            <Input title={'Stock'} type='number' value={product.Stock} name='Stock' isRequired='required' />
                            <Select title={'Pack Type'} name='Pack_Type' isRequired='required' options={unitTypes.map(u => u.Name)} />
                            <Input title={'Pack TP'} type='number' value={product.Pack_TP} name='Pack_TP' isRequired='required' />
                            <Input title={'Pack MRP'} type='number' value={product.Pack_MRP} name='Pack_MRP' isRequired='required' />
                        </div>
                        <div className="flex justify-end mt-4">
                            <SaveButton type="submit" />
                            <CancelButton 
                                type="button" 
                                onClick={() => document.getElementById(modalId).checked = false} 
                                extraClass="ml-2" 
                            />
                        </div>
                    </form>
                </label>
            </label>

            <label htmlFor={modalId} className="btn btn-xs btn-ghost">
                <BiEdit className="text-blue-500" />
            </label>
        </>
    );
};

const NonPharmacyProducts = () => {
    const [nonPharmacyProducts, setNonPharmacyProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalProducts, setTotalProducts] = useState(0);
    const [totalPages, setTotalPages] = useState(0);
    const productsPerPage = 10;
    const maxVisiblePages = 5; // Only show 5 pages at a time

    // Fetch Non-Pharmacy Products
    const fetchProducts = async (page) => {
        try {
            const response = await fetch(`${API_BASE_URL}?page=${page}&limit=${productsPerPage}`);
            const data = await response.json();
            if (response.ok) {
                setNonPharmacyProducts(data.data);
                setTotalProducts(data.totalItems || 0);
                setTotalPages(data.totalPages || 1);
            } else {
                console.error('Unexpected response format:', data);
                setNonPharmacyProducts([]);
            }
        } catch (error) {
            console.error('Error fetching non-pharmacy products:', error);
        }
    };

    // Fetch data when the page changes
    useEffect(() => {
        fetchProducts(currentPage);
    }, [currentPage]);

    // Add New Product
    const addNonPharmacyProduct = async (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        const productDetails = Object.fromEntries(formData.entries());

        productDetails.addedBy = 'admin';
        productDetails.addedToDbAt = new Date().toISOString();

        try {
            const response = await fetch(API_BASE_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(productDetails),
            });

            if (response.ok) {
                toast(<AddModal name={productDetails.tradeName} />);
                fetchProducts(currentPage); // Refresh the product list
            } else {
                console.error('Error adding product:', await response.json());
            }
        } catch (error) {
            console.error('Error:', error);
        }
        event.target.reset();
    };

    // Pagination controls
    const handlePageChange = (newPage) => {
        if (newPage >= 1 && newPage <= totalPages) {
            setCurrentPage(newPage);
        }
    };

    // Render optimized pagination with ellipsis
    const renderPagination = () => {
        const pages = [];
        let startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
        let endPage = Math.min(totalPages, startPage + maxVisiblePages - 1);

        if (endPage - startPage + 1 < maxVisiblePages) {
            startPage = Math.max(1, endPage - maxVisiblePages + 1);
        }

        // "Previous" button
        pages.push(
            <button
                key="prev"
                className={`btn btn-sm ${currentPage === 1 ? 'btn-disabled' : ''}`}
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage === 1}
            >
                Previous
            </button>
        );

        // First page and ellipsis
        if (startPage > 1) {
            pages.push(
                <button key={1} className={`btn btn-sm ${currentPage === 1 ? 'btn-active' : ''}`} onClick={() => handlePageChange(1)}>
                    1
                </button>
            );
            if (startPage > 2) {
                pages.push(<span key="start-ellipsis" className="btn btn-sm btn-disabled">...</span>);
            }
        }

        // Middle pages
        for (let i = startPage; i <= endPage; i++) {
            pages.push(
                <button key={i} className={`btn btn-sm ${currentPage === i ? 'btn-active' : ''}`} onClick={() => handlePageChange(i)}>
                    {i}
                </button>
            );
        }

        // Last page and ellipsis
        if (endPage < totalPages) {
            if (endPage < totalPages - 1) {
                pages.push(<span key="end-ellipsis" className="btn btn-sm btn-disabled">...</span>);
            }
            pages.push(
                <button key={totalPages} className={`btn btn-sm ${currentPage === totalPages ? 'btn-active' : ''}`} onClick={() => handlePageChange(totalPages)}>
                    {totalPages}
                </button>
            );
        }

        // "Next" button
        pages.push(
            <button
                key="next"
                className={`btn btn-sm ${currentPage === totalPages ? 'btn-disabled' : ''}`}
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
            >
                Next
            </button>
        );

        return pages;
    };

    return (
        <section className='p-4 mt-16'>
            <DashboardPageHeading
                name={`Non Pharmacy Products (${totalProducts})`}
                value={totalProducts}
                buttons={[
                    <NewButton key="new-button" modalId='create-new-product' />,
                    <RefreshButton key="refresh-button" onClick={() => fetchProducts(currentPage)} />,
                    <PrintButton key="print-button" />
                ]}
            />

            {/* Products Table */}
            <table className="table table-zebra table-compact">
                <thead>
                    <tr>
                        {['SN', 'Product Name', 'Category', 'Company', 'Stock', 'Pack Type', 'Pack TP', 'Pack MRP', 'Actions'].map((item, index) => (
                            <th key={index} className='text-xs md:text-2xs lg:text-md'>{item}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {nonPharmacyProducts.map((product, index) => (
                        <TableRow
                            key={product._id}
                            tableRowsData={[
                                (currentPage - 1) * productsPerPage + index + 1,
                                product.Product_name || 'N/A',
                                product.Category || 'N/A',
                                product.Company || 'N/A',
                                product.Stock || 'N/A',
                                product.Pack_Type || 'N/A',
                                product.Pack_TP || 'N/A',
                                product.Pack_MRP || 'N/A',
                                <span className='flex items-center gap-x-1'>
                                    <EditNonPharmacyButton 
                                        id={product._id} 
                                        onUpdate={() => fetchProducts(currentPage)}
                                    />
                                    <DeleteButton 
                                        deleteApiLink={`${API_BASE_URL}/${product._id}`} 
                                        itemId={product._id} 
                                        name={product.Product_name} 
                                        onDelete={() => fetchProducts(currentPage)}
                                    />
                                </span>
                            ]}
                        />
                    ))}
                </tbody>
            </table>

            {/* Pagination */}
            <div className="flex justify-center mt-4 gap-2">
                {renderPagination()}
            </div>
        </section>
    );
};

export default NonPharmacyProducts;
