import React, { useEffect, useState } from 'react';
import PrintButton from '../../../components/buttons/PrintButton';
import Input from '../../../components/form/Input';
import TableRow from '../../../components/TableRow';
import SaveButton from '../../../components/buttons/SaveButton';
import EditButton from '../../../components/buttons/EditButton';
import DeleteButton from '../../../components/buttons/DeleteButton';
import RefreshButton from '../../../components/buttons/RefreshButton';
import DashboardPageHeading from '../../../components/headings/DashboardPageHeading';
import CancelButton from '../../../components/buttons/CancelButton';
import ModalHeading from '../../../components/headings/ModalHeading';
import ModalCloseButton from '../../../components/buttons/ModalCloseButton';
import NewButton from '../../../components/buttons/NewButton';
import { toast } from 'react-toastify';

const Companies = () => {
    const tableHeadItems = ['SN', 'Name', 'Phone', 'Website', 'Email', 'Address', 'Creator', 'Created At', 'Updated By', 'Updated At', 'Actions'];

    const tableHead = <tr>
        {tableHeadItems?.map((tableHeadItem, index) => <th key={index} className='text-xs'>{tableHeadItem}</th>)}
    </tr>;

    const [companies, setCompanies] = useState([]);
    
    useEffect(() => {
        fetch('http://localhost:5000/api/setup/companies')
            .then(res => res.json())
            .then(data => setCompanies(data))
            .catch(error => console.error('Error fetching companies:', error));
    }, []);

    const addCompany = async (event) => {
        event.preventDefault();
    
        const companyName = event.target.companyName.value;
        const companyPhone = event.target.companyPhone.value;
        const companyWebsite = event.target.companyWebsite.value;
        const companyEmail = event.target.companyEmail.value;
        const companyAddress = event.target.companyAddress.value;
        
        // Check if required fields are present
        if (!companyName || !companyPhone || !companyWebsite || !companyEmail || !companyAddress) {
            toast.error("All fields are required");
            return;
        }
    
        const companyDetails = { companyName, companyPhone, companyWebsite, companyEmail, companyAddress };
    
        console.log("Sending Data:", companyDetails); // Debugging output
    
        try {
            const response = await fetch(`http://localhost:5000/api/setup/companies`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`  // Ensure the token is included
                },
                body: JSON.stringify(companyDetails),
            });
    
            const data = await response.json();
    
            if (response.ok) {
                toast.success("Company added successfully");
                setCompanies([...companies, data]); // Update the state with the new company
            } else {
                throw new Error(data.message || "Failed to add company");
            }
        } catch (error) {
            console.error("Error adding company:", error);
            toast.error(error.message);
        }
    };
    

    return (
        <section className='p-4 mt-16'>
            <DashboardPageHeading
                name='Companies'
                value={companies.length}
                buttons={[
                    <NewButton modalId='create-new-product' />,
                    <RefreshButton />,
                    <PrintButton />
                ]}
            />

            <input type="checkbox" id="create-new-product" className="modal-toggle" />
            <label htmlFor="create-new-product" className="modal cursor-pointer">
                <label className="modal-box lg:w-7/12 md:w-10/12 w-11/12 max-w-4xl relative" htmlFor="">
                    <ModalCloseButton modalId={'create-new-product'} />
                    <ModalHeading modalHeading={'Add a new Company'} />
                    <form onSubmit={addCompany} className='mx-auto'>
                        <div className='grid grid-cols-2 md:grid-cols-2 lg:grid-cols-3 gap-x-4 gap-y-2 mb-2'>
                            <Input title={'Company Name'} name='companyName' isRequired='required' type='text' />
                            <Input title={'Company Phone'} name='companyPhone' isRequired='required' type='text' />
                            <Input title={'Company Website'} name='companyWebsite' isRequired='required' type='text' />
                            <Input title={'Company Email'} name='companyEmail' isRequired='required' type='email' />
                            <Input title={'Company Address'} name='companyAddress' isRequired='required' type='text' />
                        </div>
                        <div className="flex flex-col w-full lg:flex-row mt-4 place-content-center">
                            <div className="grid">
                                <SaveButton extraClass='mt-4' />
                            </div>
                            <div className="divider lg:divider-horizontal hidden md:block lg:block"></div>
                            <div className="grid">
                                <CancelButton extraClass='lg:mt-4 md:mt-3 mt-2' />
                            </div>
                        </div>
                    </form>
                </label>
            </label>
            <table className="table table-zebra table-compact w-full">
                <thead>
                    {tableHead}
                </thead>
                <tbody>
                    {companies.map((company, index) => (
                        <TableRow key={company._id} tableRowsData={[
                            index + 1,
                            company.Name,
                            company.Phone,
                            company.Website,
                            company.Email,
                            company.Address,
                            company.Creator,
                            company?.CreatedAt?.slice(0, 10),
                            company.UpdatedBy,
                            company?.UpdatedAt?.slice(0, 10),
                            <span className='flex items-center gap-x-1'>
                                <EditButton />
                                <DeleteButton deleteApiLink={`http://localhost:5000/api/setup/companies/${company._id}`} itemId={company._id} name={company.Name} />
                            </span>
                        ]} />
                    ))}
                </tbody>
            </table>
        </section>
    );
};

export default Companies;
